    def next(token):
        if token in FancyWords.chain:
            k, v = zip(*FancyWords.chain[token].items())
            k = list(k)
            v = list(v)
            weights = []
            s = 0
            for i in range(len(v)):
                weights.append(v[i] + s)
                s += v[i]
            r = random.randrange(s)

            # Find corresponding index
            for i in range(len(weights)):
                if r < weights[i]:
                    break

            return k[i]

    def generate():
        min_len = 4
        max_len = 8    # No guarantee the generated word will not be longer
        phrase = ['[']
        while phrase[-1] != ']':
            n = FancyWords.next(tuple(phrase[-2:]))
            if not n:
                print(phrase)
            if len(phrase) >= max_len and n in "aeio":
                phrase.append(n)
                return ''.join(phrase[1:])
            while len(phrase) <= min_len and n == ']':
                n = FancyWords.next(tuple(phrase[-2:]))
            phrase.append(n)

        return ''.join(phrase[1:-1])

